import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import styled from 'styled-components';
import Dashboard from './components/Dashboard';
import StudyPlanner from './components/StudyPlanner';
import CampusResources from './components/CampusResources';
import StudyBuddy from './components/StudyBuddy';
import Auth from './components/Auth';
import ErrorBoundary from './components/ErrorBoundary';

const AppContainer = styled.div`
  font-family: Arial, sans-serif;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const Nav = styled.nav`
  background-color: #1a237e;
  padding: 1rem;
  margin-bottom: 20px;
  border-radius: 8px;
`;

const NavLink = styled(Link)`
  color: white;
  text-decoration: none;
  margin-right: 1rem;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #3949ab;
  }
`;

const LogoutButton = styled.button`
  color: white;
  background: none;
  border: none;
  cursor: pointer;
  font-size: 1rem;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  transition: background-color 0.3s;

  &:hover {
    background-color: #3949ab;
  }
`;

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setUser({ username: 'User' });
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <Router>
      <ErrorBoundary>
        <AppContainer>
          {user ? (
            <>
              <Nav>
                <NavLink to="/">Dashboard</NavLink>
                <NavLink to="/study-planner">Study Planner</NavLink>
                <NavLink to="/campus-resources">Campus Resources</NavLink>
                <NavLink to="/study-buddy">Study Buddy</NavLink>
                <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
              </Nav>
              <Routes>
                <Route path="/" element={<Dashboard />} />
                <Route path="/study-planner" element={<StudyPlanner />} />
                <Route path="/campus-resources" element={<CampusResources />} />
                <Route path="/study-buddy" element={<StudyBuddy />} />
                <Route path="*" element={<Navigate to="/" replace />} />
              </Routes>
            </>
          ) : (
            <Auth setUser={setUser} />
          )}
        </AppContainer>
      </ErrorBoundary>
    </Router>
  );
}

export default App;
